Compose_ModulePct.inputs:
@mul(
  if(equals(outputs('Get_Student_Summary_Exact_Item')?['body/Class'],'3456'),70,80),
  min(
    if(
      equals(float(variables('ModuleHours')), 0),
      0,
      div(
        float(variables('ModuleHours')),
        float(outputs('Compose_ExpectedHours'))
      )
    ),
    1
  )
)

Compose_DaysSinceStart.inputs:
@if(
  lessOrEquals(utcNow(), variables('StartDate')),
  0,
  if(
    greaterOrEquals(utcNow(), variables('EndDate')),
    outputs('Compose_DaysInSemester'),
    add(
      div(
        sub(ticks(utcNow()), ticks(variables('StartDate'))),
        864000000000
      ),
      1
    )
  )
)

Compose:_ExtraCreditHours.inputs:
@add(
        add(
            add(
                if(equals(outputs('Get_Student_Summary_Exact_Item')?['body/LabMeeting1'], true), 0.5, 0),
                if(equals(outputs('Get_Student_Summary_Exact_Item')?['body/LabMeeting2'], true), 0.5, 0)
            ),
            if(equals(outputs('Get_Student_Summary_Exact_Item')?['body/LabMeeting3'], true), 0.5, 0)
        ),
        if(equals(outputs('Get_Student_Summary_Exact_Item')?['body/LabMeeting4'], true), 0.5, 0)
    )
